import urllib.request as urimport re# 例1def schedule(a, b, c):    '''    a:已经下载的数据块    b:数据库块的大小    c:远程文件的大小    '''    per = 100.0 * a * b / c    if per > 100:        per = 100        print('完成！')    print('%.2f%%' % per)def gethtml(url):    page = urllib.request.urlopen(url)    html = page.read()    return htmldef getimg(html):    html = html.decode('utf-8')    imgre = re.compile(r'src="(.*?\.jpg)" width')    imglist = imgre.findall(html)    x = 0    for imgurl in imglist:        urllib.request.urlretrieve(imgurl, 'E:\\test\\%s.jpg' % x, schedule)        x += 1html = gethtml('http://tieba.baidu.com/p/741081023')print(getimg(html))# 例2url = "https://www.baidu.com"res = ur.urlopen(url)print(res)print(res.read().decode('utf-8'))